mapper.select:
select * from ( select rank() over( order by ref desc, step ) no, b.* from ( select * from board
name like '%' || #{name} || '%'
or subject like '%' || #{subject} || '%'
or content like '%' || #{content} || '%'
) b ) where no between #{start} and #{end}

mapper.update:
update board set subject=#{subject},
				 name=#{name},
				 content=#{content}
				 where idx=#{idx}
